import React, { useState } from 'react';

const Statistics = (props) => {
  let all = props.good + props.neutral + props.bad;
  let avarage = (props.good*1+props.neutral*0+props.bad*(-1))/all;
  let newValue = (isNaN(avarage) ? 0 : (avarage));
  let pos = (props.good/all)*100;
  let positive = (isNaN(pos) ? 0 : (pos));
  return (
    <div>
      <h1>Statistics</h1>
      <div>good: {props.good}</div>
      <div>bad: {props.bad}</div>
      <div>neutral: {neutral}</div>
      <div>the average score: {newValue}</div>
      <div>the total number: {all}</div>
      <div>the percentage of positive feedback: {positive}%</div>
    </div>
  );
}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);


  const increaseGood = () => setGood(good + 1);
  const increaseNeutral = () => setNeutral(neutral + 1);
  const increaseBad = () => setBad(bad + 1);

  return (
    <div>
      <div className="feedback">
        <h1>Give feedback</h1>
        <button onClick={increaseGood}>Good</button>
        <button onClick={increaseNeutral}>Neutral</button>
        <button onClick={increaseBad}>Bad</button>
      </div>

      <div>
        <Statistics good={good} neutral={neutral} bad={bad}/>
      </div>

    </div>
  );
}

export default App;
